INSERT INTO `scenic_spot` VALUES ('0', '鲁迅故居', '5', '上海', '56662608', '山阴路132弄大陆新邨9号', '名人故居', '121.484291', '31.266134', '鲁迅故居位于中国江苏省南京市鼓楼区，原为鲁迅先生晚年居住之地，也是他创作《阿Q正传》等作品的地方。该处展示了鲁迅的生活轨迹和文化遗产，保留了他的书房、起居室等部分原貌，展览了他的书籍、手稿及个人物品，是了解鲁迅先生及其思想的重要场所。', NULL);
INSERT INTO `scenic_spot` VALUES ('1', '上海鲁迅纪念馆', '5', '上海', '65402288', '欧阳路街道甜爱路200号（近四川北路）', '纪念馆', '121.483765', '31.269737', '上海鲁迅纪念馆位于上海市静安区欧阳路200号，是为纪念中国伟大的民主主义文学家鲁迅而建立的。馆内展示了鲁迅的生平事迹、文学作品、手稿、书信、照片等，是了解鲁迅先生及其思想的重要场所。', NULL);
