INSERT INTO `scenic_spot` VALUES ('15146', '杜甫江阁', '10', '长沙', '89824455', '湘江中路108号', '现代建筑', '112.968613', '28.184455', '杜甫江阁位于中国四川省成都市青羊区，是一座纪念唐代诗人杜甫的文化景区。这座景区建于杜甫故居遗址，以杜甫的诗文和文化为主题，展示了杜甫的生平事迹和文学成就。游客可以在这里参观杜甫的居所遗址、阅读他的诗篇，感受其诗人风采和成就对中国文学的重要影响。杜甫江阁也是成都市内重要的历史文化景点之一，吸引了众多文学爱好者和游客前来参观。', NULL);
