INSERT INTO `scenic_spot` VALUES ('17363', '中山桥', '18', '兰州', '139****4533', '中山路与南滨河东路交叉口北侧', '地标建筑', '103.817009', '36.063519', '中山桥，旧名第一桥，又称兰州黄河铁桥，是位于中华人民共和国甘肃省兰州市城关区的一座钢桁架桥，坐落在白塔山下，横跨黄河两岸，是兰州市著名的旅游景点和地标之一。于清朝宣统元年七月初四（1909年8月19日）建成通行，成为兰州历史上第一座跨越黄河的永久性桥梁。', NULL);
