INSERT INTO `scenic_spot` VALUES ('3', '南京钟藏室', '5', '上海', '56964048', '多伦路191号（近四川北路）', '博物馆', '121.481392', '31.263409', '南京钟藏室位于上海市虹口区多伦路191号，是一家专门展示中国古代钟表文化的博物馆。馆内展示了各种古代钟表，如日晷、水钟、机械钟等，是了解中国古代科技文化的重要场所。', NULL);
INSERT INTO `scenic_spot` VALUES ('30', '外滩', '5', '上海', '4000998998', '中山东一路与延安东路交叉口东北角', '河道观光', '121.493637', '31.232021', '外滩位于中国上海市黄浦江畔，是上海的标志性景点之一。这里以其壮丽的滨江风光和历史建筑群闻名于世，包括不少具有欧洲建筑风格的历史建筑，如“十六铺”、“信号台”等，展示了上海百年来的变迁与发展。外滩不仅是游客观光的热门地点，也是商业活动和文化交流的重要场所，夜晚灯火辉煌，成为上海城市夜景的重要组成部分。', NULL);
