INSERT INTO `scenic_spot` VALUES ('3673', '鼓浪屿', '13', '厦门', '3533188', '永春路鼓浪屿', '自然风光', '118.067007', '24.444719', '鼓浪屿位于中国福建省厦门市，是厦门市辖下的一个小岛，也是厦门最著名的旅游景点之一。鼓浪屿以其独特的自然风光、浓厚的历史文化和特色建筑而闻名于世。岛上有许多欧式建筑、日式建筑和中西合璧的建筑风格，反映了不同历史时期的文化交流和遗产保留。著名的景点包括日光岩、鼓浪屿风琴博物馆、厦门博物院、皓月园、曾厝垵等。鼓浪屿还以其美丽的海滩和清新的空气吸引着众多游客，尤其是夏季成为避暑度假的热门去处。作为中国历史文化名岛之一，鼓浪屿被列为世界文化遗产，也是厦门城市的文化象征和重要的旅游胜地。', NULL);
