INSERT INTO `scenic_spot` VALUES ('7112', '成都大熊猫繁育研究基地', '1', '成都', '83510033', '熊猫大道1375号', '动物园', '104.139483', '30.740105', '成都大熊猫繁育研究基地位于中国四川省成都市，是全球知名的大熊猫保护和繁育中心。成立于1987年，基地致力于大熊猫的科学研究、保护和教育。游客可以在这里近距离观赏大熊猫的日常生活，了解它们的习性和生态环境。基地不仅是大熊猫保护的核心地，也是国际上研究和保护大熊猫的重要基地之一。', NULL);
INSERT INTO `scenic_spot` VALUES ('7113', '成都动物园', '1', '成都', '83516651;83519606', '青龙街道昭觉寺南路234号（动物园地铁站A口步行260米）', '动物园', '104.10592', '30.71011', '', NULL);
INSERT INTO `scenic_spot` VALUES ('7114', '成都杜甫草堂博物馆', '1', '成都', '87345202;87319258', '青华路37号', '名人故居', '104.02824', '30.660167', '成都杜甫草堂博物馆位于中国四川省成都市青羊区，是著名的文化遗址和旅游景点。这座博物馆是为了纪念唐代著名诗人杜甫而建立的，杜甫曾在此居住过。草堂建筑主要包括主楼、东西厢房、小花园和青瓦披靡的古朴建筑风格，展示了中国传统的文人雅居风貌。', NULL);
