INSERT INTO `scenic_spot` VALUES ('7123', '成都武侯祠博物馆', '1', '成都', '87198667', '武侯祠大街231号', '博物馆', '104.048042', '30.646208', '成都武侯祠博物馆位于中国四川省成都市武侯区，是为了纪念中国古代蜀汉名将诸葛亮而建立的文化遗址和博物馆。武侯祠内主要有诸葛亮的历史遗物、文物以及相关展品，展示了他的生平、思想及其对中国历史的重要影响。这里不仅是学习和了解中国古代历史的重要场所，也是蜀汉文化和诸葛亮精神的象征和传承地。', NULL);
