INSERT INTO `scenic_spot` VALUES ('7132', '都江堰景区', '1', '成都', '96526', '公园路', '古代遗迹', '103.609041', '31.001596', '都江堰景区位于中国四川省成都市都江堰市，是一处著名的世界文化遗产和旅游胜地。都江堰是中国古代著名水利工程，始建于公元前256年，是世界上最古老、最早的灌溉工程之一，也是中国四大古代水利工程之一。', NULL);
